mapper.select:
SELECT id, goods_name AS goodsName,price,category1_id AS category1Id, category2_id AS category2Id,category3_id AS category3Id, audit_status AS auditStatus, is_marketable as isMarketable FROM tb_goods where is_delete IS NULL
AND seller_id = #{sellerId}
AND audit_status = #{auditStatus}
AND goods_name like CONCAT('%',#{goodsName},'%')
